import React, { useState } from 'react';
import { Search, Building2, Briefcase, MapPin } from 'lucide-react';

const MultiSelect = ({ options, value, onChange, placeholder }) => (
  <div className="relative">
    <select
      multiple
      value={value}
      onChange={e => onChange([...e.target.selectedOptions].map(opt => opt.value))}
      className="w-full rounded-lg border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
    >
      {options.map(option => (
        <option key={option} value={option}>
          {option.charAt(0).toUpperCase() + option.slice(1)}
        </option>
      ))}
    </select>
    {value.length > 0 && (
      <button
        onClick={() => onChange([])}
        className="absolute right-2 top-2 text-gray-400 hover:text-gray-600"
        type="button"
      >
        Clear
      </button>
    )}
  </div>
);

const PDLSearch = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedIndustries, setSelectedIndustries] = useState([]);
  const [selectedLevels, setSelectedLevels] = useState([]);
  const [selectedSizes, setSelectedSizes] = useState([]);
  const [minExp, setMinExp] = useState(0);
  const [maxExp, setMaxExp] = useState(40);
  const [scrollToken, setScrollToken] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const INDUSTRIES = [
    "computer software",
    "information technology and services",
    "financial services",
    "banking",
    "healthcare",
    "marketing and advertising",
    "telecommunications",
    "consulting",
    "education",
    "retail",
    "insurance",
    "real estate",
    "automotive",
    "entertainment",
    "pharmaceuticals",
    "construction",
    "hospitality",
    "manufacturing",
    "media",
    "e-commerce"
  ];

  const JOB_LEVELS = [
    "intern",
    "entry",
    "senior",
    "manager",
    "director",
    "vp",
    "cxo",
    "owner"
  ];

  const COMPANY_SIZES = [
    "1-10",
    "11-50",
    "51-200",
    "201-500",
    "501-1000",
    "1001-5000",
    "5001-10000",
    "10001+"
  ];

  const handleSearch = async (e, isLoadingMore = false) => {
    e?.preventDefault();
    setLoading(true);

    const formData = isLoadingMore ? null : new FormData(e.target);
    const searchParams = {
      query: {
        bool: {
          must: []
        }
      },
      size: 10
    };

    // Add scroll token if loading more
    if (isLoadingMore && scrollToken) {
      searchParams.scroll_token = scrollToken;
    } else {
      // Clear previous results if this is a new search
      setSearchResults([]);
      setScrollToken(null);
    }

    // Add job title
    const jobTitle = isLoadingMore ? document.getElementById('jobTitle').value : formData.get('jobTitle');
    if (jobTitle) {
      searchParams.query.bool.must.push({
        match: { "job_title": jobTitle }
      });
    }

    // Add company name
    const companyName = isLoadingMore ? document.getElementById('companyName').value : formData.get('companyName');
    if (companyName) {
      searchParams.query.bool.must.push({
        match: { "job_company_name": companyName }
      });
    }

    if (selectedIndustries.length) {
      searchParams.query.bool.must.push({
        terms: { "job_company_industry": selectedIndustries }
      });
    }

    if (selectedLevels.length) {
      searchParams.query.bool.must.push({
        terms: { "job_title_levels": selectedLevels }
      });
    }

    if (selectedSizes.length) {
      searchParams.query.bool.must.push({
        terms: { "job_company_size": selectedSizes }
      });
    }

    // Add experience range
// Update the range query in handleSearch:
if (minExp > 0 || maxExp < 40) {
  searchParams.query.bool.must.push({
    bool: {
      must: [
        // Must have a value for inferred_years_experience
        { exists: { field: "inferred_years_experience" } },
        // Must be within range
        {
          range: {
            "inferred_years_experience": {
              gte: minExp,
              lte: maxExp
            }
          }
        }
      ]
    }
  });
}

    try {
      const response = await fetch('http://localhost:8081/api/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(searchParams)
      });

      const data = await response.json();

      if (data.data) {
        setSearchResults(prev => isLoadingMore ? [...prev, ...data.data] : data.data);
        setScrollToken(data.scroll_token);
        setHasMore(!!data.scroll_token);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-6xl mx-auto space-y-8">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gray-900">PDL Recruiter Search</h1>
          <p className="mt-2 text-lg text-gray-600">Find the perfect candidates for your roles</p>
        </div>

        <div className="bg-white rounded-lg shadow-sm p-6">
          <form onSubmit={handleSearch} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Job Title
                </label>
                <div className="relative">
                  <Briefcase className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <input
                    id="jobTitle"
                    name="jobTitle"
                    className="pl-10 w-full rounded-lg border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                    placeholder="e.g. Software Engineer"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Company
                </label>
                <div className="relative">
                  <Building2 className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <input
                    id="companyName"
                    name="companyName"
                    className="pl-10 w-full rounded-lg border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                    placeholder="e.g. Google"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Industry
                </label>
                <MultiSelect
                  options={INDUSTRIES}
                  value={selectedIndustries}
                  onChange={setSelectedIndustries}
                  placeholder="Select industries"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Job Level
                </label>
                <MultiSelect
                  options={JOB_LEVELS}
                  value={selectedLevels}
                  onChange={setSelectedLevels}
                  placeholder="Select job levels"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Company Size
                </label>
                <MultiSelect
                  options={COMPANY_SIZES}
                  value={selectedSizes}
                  onChange={setSelectedSizes}
                  placeholder="Select company sizes"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Years of Experience
                </label>
                <div className="flex gap-2 items-center">
                  <input
                    type="number"
                    min="0"
                    max={maxExp}
                    value={minExp}
                    onChange={e => setMinExp(Number(e.target.value))}
                    className="w-20 rounded-lg border border-gray-300 shadow-sm p-2"
                  />
                  <span>to</span>
                  <input
                    type="number"
                    min={minExp}
                    max="40"
                    value={maxExp}
                    onChange={e => setMaxExp(Number(e.target.value))}
                    className="w-20 rounded-lg border border-gray-300 shadow-sm p-2"
                  />
                  <button
                    type="button"
                    onClick={() => {
                      setMinExp(0);
                      setMaxExp(40);
                    }}
                    className="text-sm text-gray-400 hover:text-gray-600"
                  >
                    Reset
                  </button>
                </div>
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Searching...
                </span>
              ) : (
                <span className="flex items-center justify-center">
                  <Search className="mr-2 h-4 w-4" />
                  Search Candidates
                </span>
              )}
            </button>
          </form>
        </div>

        {searchResults.length > 0 && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold mb-4">Search Results</h2>
            <div className="space-y-6">
              {searchResults.map((person, index) => (
                <div key={index} className="border-b last:border-0 pb-4 last:pb-0">
                  <div className="flex items-start justify-between">
                    <div>
                      <h3 className="font-semibold text-lg">{person.full_name || 'N/A'}</h3>
                      <p className="text-sm text-gray-600">{person.job_title || 'N/A'}</p>
                    </div>
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                      {person.inferred_years_experience || 'N/A'} years exp
                    </span>
                  </div>

                  <div className="mt-2 space-y-1">
                    <div className="flex items-center text-sm text-gray-600">
                      <Building2 className="mr-2 h-4 w-4" />
                      {person.job_company_name || 'N/A'}
                    </div>
                    <div className="flex items-center text-sm text-gray-600">
                      <MapPin className="mr-2 h-4 w-4" />
                      {person.location_name || 'N/A'}
                    </div>
                    {person.skills?.length > 0 && (
                      <div className="flex flex-wrap gap-1 mt-2">
                        {person.skills.slice(0, 5).map((skill, i) => (
                          <span
                            key={i}
                            className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-gray-100 text-gray-800"
                          >
                            {skill}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {hasMore && (
              <div className="mt-4 text-center">
                <button
                  onClick={(e) => handleSearch(e, true)}
                  disabled={loading}
                  className="bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                >
                  {loading ? 'Loading...' : 'Load More Results'}
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default PDLSearch;
